mapper.select:
SELECT CATEGORY_ID, CATEGORY_NAME, PARENT_CATEGORY_ID, CATEGORY_SEQ, IS_DISPLAY FROM CATEGORY
AND PARENT_CATEGORY_ID = #{categoryId}
ORDER BY CATEGORY_SEQ ASC

mapper.update:
UPDATE CATEGORY
CATEGORY_NAME = #{categoryName}, CATEGORY_SEQ = #{categorySeq}, PARENT_CATEGORY_ID = #{parentCategoryId}, IS_DISPLAY = #{isDisplay}, UPDATED_AT = NOW()
WHERE CATEGORY_ID = #{categoryId}


SET @seq=${categorySeq};

UPDATE CATEGORY
SET CATEGORY_SEQ = @seq:=@seq+1
WHERE PARENT_CATEGORY_ID = ${parentCategoryId}
  AND CATEGORY_SEQ >= ${categorySeq}
ORDER BY CATEGORY_SEQ ASC
